import React from "react";
import "./index.css";
import Navbar from "./components/Navbar";
import Hero from "./components/Hero";
import Card from "./components/Card";
// import data from "./data";

function App() {
  // cards section
  const data = [
    {
      id: 1,
      title: "Life Lessons with Katie Zaferes",
      description: "Interested in blablabla",
      price: 136,
      coverImg: "katie-zaferes.png",
      stats: {
        rating: "5.0",
        reviewCount: 6,
      },
      country: "USA",
      openSpots: 0,
    },
    {
      id: 2,
      title: "Learn Wedding Photografer",
      description: "Interested in blablabla",
      price: 126,
      coverImg: "wedding-photography 1.png",
      stats: {
        rating: "5.0",
        reviewCount: 30,
      },
      country: "America",
      openSpots: 26,
    },
    {
      id: 3,
      title: "Group Mountain Biking",
      description: "Biking",
      price: 136,
      coverImg: "mountain-bike 1.png",
      stats: {
        rating: "4.8",
        reviewCount: 2,
      },
      country: "Mexico",
      openSpots: 50,
    },
  ];

  const cards = data.map((item) => {
    return (
      <Card
        img={item.coverImg}
        rating={item.stats.rating}
        reviewCount={item.stats.reviewCount}
        country={item.location}
        title={item.title}
        price={item.price}
      />
    );
  });

  return (
    <>
      <Navbar />
      <Hero />
      {cards}
    </>
  );
}

export default App;
